import React, { useState } from 'react'
import { Button } from 'semantic-ui-react'

import Styles from './yes-or-no.module.css'

const YesOrNo = () => {
  const [state, setState] = useState('YES')
  const { buttonContainer, container } = Styles
  const randomValue = () => {
    const options = ['YES', 'NO']
    return setState(Math.floor(Math.random() * options.length))
  }

  return (
    <div className={container}>
      <div></div>
      <div className={buttonContainer}>
        <Button color="green" onClick={randomValue}>
          Intentar de nuevo
        </Button>
      </div>
      <div className={buttonContainer}>
        <Button>Volver al inicio</Button>
      </div>
    </div>
  )
}

export default YesOrNo
